Map<String, Object> attributes = new HashMap<String, Object>();



Float budgetedAmount = new Float(0);

Float capitalAmount = new Float(0);

Float capitalSalesTax = new Float(0);

Float expenseAmount = new Float(0);

Float expenseSalesTax = new Float(0);

Float deferredAmount = new Float(0);



if (field_budgeted.equals("In Budget")){

  if (field_budgetedAmount != null && field_budgetedAmount.length() > 0 && field_budgetedAmount.replaceAll(",","") ==~ /\d*(\.\d+)?/)

	budgetedAmount = Float.parseFloat(field_budgetedAmount.replaceAll(",",""));

}



attributes.put("InBudget", field_budgeted);

attributes.put("BudgetedAmount", budgetedAmount);

