  attributes.put("IntOrderNumber",      );

  attributes.put("ProjectNumber",      );

  attributes.put("CapitalAmount", capitalAmount);

  attributes.put("ExpenseAmount", expenseAmount);

  attributes.put("CapitalSalesTax", capitalSalesTax);

  attributes.put("ExpenseSalesTax", expenseSalesTax);

  attributes.put("DeferredAmount", deferredAmount);

}



attributes.put("SuplimentalRequest", field_suplimental);



if (field_suplimental.equals("Yes")) {

	attributes.put("OriginalAFENumber", field_oiginalAFENumber);

	attributes.put("OriginalAFEName", field_oiginalAFEName);

	attributes.put("OriginalAFETotal", field_originalAFETotal);

